import React from "react";
import Loader from "react-loader-spinner";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import "./Loading.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPowerOff,
  faLocationArrow,
  faSearch,
} from "@fortawesome/free-solid-svg-icons";

export default function Loading() {
  const powerBtn = <FontAwesomeIcon icon={faPowerOff} size='3x' />;
  return (
    <div className='loading'>
      <div className='loading-container col-md-4 col-sm-8'>
        <div className='row'>
          <div className='col-12'>
            <h1 className='loading-title'>Enter a city. . .</h1>
            <form className='row mt-5'>
              <div className='col-md-10'>
                <input
                  type='text'
                  placeholder='Enter city'
                  className='form-control'
                />
              </div>
              <button className='small-btn'>
                <FontAwesomeIcon icon={faLocationArrow} size='1x' />
              </button>
              <button className='small-btn'>
                <FontAwesomeIcon icon={faSearch} size='1x' />
              </button>
            </form>
          </div>
          <div className='col-12 pt-5 loader-icon'>
            <Loader type='Circles' color='#2c1148' height={200} width={200} />
          </div>
          <div className='col-12'>
            <button className='lightBtn'>{powerBtn}</button>
          </div>
        </div>
      </div>
    </div>
  );
}
